mapper.update:
update city
set city_name = #{cityName,jdbcType=VARCHAR},
  city_lon = #{cityLon,jdbcType=DOUBLE},
  city_lat = #{cityLat,jdbcType=DOUBLE}
where city_id = #{cityId,jdbcType=INTEGER}

mapper.select:
select city_id, city_name, city_lon, city_lat
from city
where city_id = #{cityId,jdbcType=INTEGER}
